operations.schema:
input AddAccountInput {
    address: String!
    name: String
    lineItems: [LineItemInput!]
}

input UpdateAccountInput {
    address: String!
    name: String
    lineItems: [LineItem!]
}

input DeleteAccountInput {
    account: String!
}

input SortAccountsInput {
    accounts: [String!]!
}

input DeleteLineItemInput {
 accountId: ID!
 group: String
 category: String }

input LineItemsSortInput {
 group: String
 category: String }

input SortLineItemsInput {
 accountId: ID!
 lineItems: [LineItemsSortInput!]! }

input SetOwnerInput {
 ref: String
 id: String
 title: String }

input FtesForecastInput {
 month: String!
 value: Float! }

input SetFtesInput {
 value: Float!
 forecast: [FtesForecastInput!]! }

input AddAuditReportInput {
timestamp: DateTime
report: Attachment!
status: AuditReportStatus!
}

input DeleteAuditReportInput {
report: String!
}

input CommentAuthorInput {
 ref: String
 id: String
 username: String
 roleLabel: String }

input AddCommentInput {
 key: String
 author: CommentAuthorInput
 comment: String!
 timestamp: DateTime
 status: BudgetStatus }

input UpdateCommentInput {
 key: String!
 author: CommentAuthorInput
 comment: String
 timestamp: DateTime
 status: BudgetStatus }
